import React from 'react'
import { View, Text, Button, TouchableOpacity } from 'react-native'
import { TextInput } from 'react-native-gesture-handler'
import DocumentPicker from 'react-native-document-picker';

export default class OpenFile extends React.Component {
  static navigationOptions = {
    headerShown: false
  }

  state = {
    uri:'https://www.rfc-editor.org/rfc/rfc2616.pdf',
    base:''
  }

  _onPress = (uri) => {
    if (uri.split('/')[0].includes('https')) {
      this.props.navigation.navigate('pdf', {uri:this.state.uri, title:this._getTitle(this.state.uri), isBase64:false})
    } else {
      this.props.navigation.navigate('pdf', {uri:this.state.base, isBase64:true})
    }
  }

  _getTitle = (path) => {
    const temp = path.split('/')
    return temp[temp.length-1]
  }

  render() {
    return (
      <View style={{flex:1, justifyContent:'center', alignItems:'center'}}>
        <Text style={{color:'green'}}>PDF 파일 오픈창</Text>
        <TextInput 
          onChangeText={text => {
            this.setState({
              uri:text
            })
          }}
          placeholder="PDF URI 를 입력하세요"
          value={this.state.uri}
          style={{borderColor:'green', borderWidth:1, marginTop:10, paddingRight:150}}
        />
        <TouchableOpacity 
          onPress={() => {
            this._onPress(this.state.uri)
          }}
        >
          <Text style={{borderWidth:1, borderColor:'green', backgroundColor:'green', color:'white', paddingLeft: 15, paddingRight: 15, marginTop:10}}>uri 입력</Text>
        </TouchableOpacity>
        <TextInput 
          onChangeText={text => {
            this.setState({
              uri:text
            })
          }}
          placeholder="base64 를 입력하세요"
          value={this.state.base}
          style={{borderColor:'green', borderWidth:1, marginTop:10, paddingRight:150}}
        />
        <TouchableOpacity 
          onPress={() => {
            this._onPress(this.state.base)
          }}
        >
          <Text style={{borderWidth:1, borderColor:'green', backgroundColor:'green', color:'white', paddingLeft: 15, paddingRight: 15, marginTop:10}}>base64 입력</Text>
        </TouchableOpacity>
        <TouchableOpacity 
          onPress={ async () => {
            try {
              const res = await DocumentPicker.pick({
                type: [DocumentPicker.types.pdf],
              });
              this.props.navigation.navigate('pdf', {uri:res.uri, title:res.name})
            } catch (err) {
              if (DocumentPicker.isCancel(err)) {
                // User cancelled the picker, exit any dialogs or menus and move on
              } else {
                throw err;
              }
            }
          }}
        >
          <Text style={{borderWidth:1, borderColor:'green', backgroundColor:'green', color:'white', paddingLeft: 15, paddingRight: 15, marginTop:10}}>파일로 불러오기</Text>
        </TouchableOpacity>
      </View>
    )
  }
}